mapper.update:
UPDATE USER_INFO
SET token = #{token}
WHERE USER_EMAIL = #{user_email}

UPDATE USER_INFO
SET USER_PW = #{user_pw}
WHERE TOKEN = #{token}

UPDATE USER_INFO
SET USER_NICK = #{user_nick}
WHERE USER_EMAIL = #{user_email}

UPDATE USER_INFO
SET USER_PW = #{new_pw}
WHERE USER_EMAIL = #{user_email} AND USER_PW = #{user_pw}

UPDATE USER_INFO
SET CHARGE_TYPE_NO = #{charge_type_no}
WHERE USER_EMAIL = #{user_email}

mapper.select:
SELECT user_email
FROM USER_INFO
WHERE token = #{token}

mapper.update:
UPDATE USER_INFO
SET token = ''
WHERE USER_EMAIL = #{user_email}

mapper.select:
SELECT count(*)
FROM USER_INFO
WHERE TOKEN = #{token}

SELECT user_no
FROM USER_INFO
WHERE USER_EMAIL = #{user_email}

SELECT CHARGE_TYPE
FROM CAR_INFO
WHERE CHARGE_TYPE_NO = #{charge_type_no}
